public class variableclass1{
public static void main(String[]args){
	Scanner input = new Scanner(System.in);
//	Float
	System.out.print("Enter any float: ");
	float myfloat = input.nextFloat();
	System.out.println("The float entered: " +myfloat);
//	Double
	System.out.print("Enter any double value:");
	double mydouble = input.nextDouble();
	System.out.println("The double value entered: " +mydouble);
//	String
	System.out.print("Enter any text:");
	String mystring = input.next();
	System.out.println("The text you entered: " +mystring);
	
}
}



